import React, { Component } from 'react'
import Shelf from '../components/Shelf'
import * as BooksAPI from '../BooksAPI'
import { Link } from 'react-router-dom'
export class Home extends Component {
    state = {
        books: []
    };

    componentDidMount() {

        BooksAPI.getAll()
            .then(
                (books) => {
                    this.setState(() => ({
                        books
                    }))
                }
            )


    }

    moveBook = (book, newShelf, allShelf) => {
        //console.log(newShelf);
        /*         const newBooks = this.state.books.map(filterdBooks => {
                    const foundID = allShelf[newShelf].find(
                        bookID => bookID === filterdBooks.id
                    )
                    if (foundID) {
                        filterdBooks.shelf = newShelf;
                    }
                    return filterdBooks
                }) */
        BooksAPI.getAll({ id: book.id }, newShelf)
            .then(
                (books) => {
                    this.setState(() => ({
                        books
                    }))
                }
            )
        console.log(book, newShelf, allShelf)
        console.log(this.state)
    }

    render() {

        const currentlyReading = this.state.books.filter(books => books.shelf === 'currentlyReading')
        const wantToRead = this.state.books.filter(books => books.shelf === 'wantToRead')
        const read = this.state.books.filter(books => books.shelf === 'read')
        return (
            <div>
                <div className="list-books">
                    <div className="list-books-title">
                        <h1>MyReads</h1>
                    </div>
                    <div className="list-books-content">
                        <Shelf title="Currently Reading" book={currentlyReading} moveBook={this.moveBook} />
                        <Shelf title="Want to read" book={wantToRead} moveBook={this.moveBook} />
                        <Shelf title="Read" book={read} moveBook={this.moveBook} />
                    </div>
                    <div className="open-search">
                        <Link to='/search' className='open-search'></Link>
                        <button onClick={() => this.setState({ showSearchPage: true })}>Add a book</button>
                    </div>
                </div>
            </div>
        )
    }
}

export default Home
