import React from 'react'
// import * as BooksAPI from './BooksAPI'
import './App.css'
import { Switch, Route } from 'react-router-dom'
import Home from './Views/Home'
import Search from './Views/Search'
import * as BooksAPI from './BooksAPI'
class BooksApp extends React.Component {
  state = {
    books: []
  };

  componentDidMount() {

    BooksAPI.getAll()
      .then(
        (books) => {
          this.setState(() => ({
            books
          }))
        }
      )


  }

  moveBook = (book, newShelf, allShelf) => {
    //console.log(newShelf);
    /*         const newBooks = this.state.books.map(filterdBooks => {
                const foundID = allShelf[newShelf].find(
                    bookID => bookID === filterdBooks.id
                )
                if (foundID) {
                    filterdBooks.shelf = newShelf;
                }
                return filterdBooks
            }) */
    BooksAPI.getAll({ id: book.id }, newShelf)
      .then(
        (books) => {
          this.setState(() => ({
            books
          }))
        }
      )
    console.log(book, newShelf, allShelf)
    console.log(this.state)
  }


  render() {
    return (
      <div className="app">
        <Switch>
          <Route exact path="/" render={() => (
            <Home books={this.state.booksbooks} changeShelf={this.changeShelf} />
          )} />
          <Route exact path={'/search'} component={Search} />
        </Switch>

      </div>
    )
  }
}

export default BooksApp
