import React, { Component } from 'react'
import Shelf from '../components/Shelf'
import * as BooksAPI from '../BooksAPI'
export class Home extends Component {
    state = {
        books: []
    };

    componentDidMount() {

        BooksAPI.getAll()
            .then(
                (books) => {
                    this.setState(() => ({
                        books
                    }))
                }
            )


    }
    changeShelf = (book, shelf) => {
        console.log( book.id )
        BooksAPI.update({ book }, shelf)
            .then(() => {

                //book.shelf = shelf
                this.setState(state => ({
                    books: state.books.filter(b => b.id !== book.id).concat(book)
                }))
            })
         }

    render() {
        const currentlyReading = this.state.books.filter(books => books.shelf === 'currentlyReading')
        const wantToRead = this.state.books.filter(books => books.shelf === 'wantToRead')
        const read = this.state.books.filter(books => books.shelf === 'read')
        console.log(this.state.books)
        return (
            <div>
                <div className="list-books">
                    <div className="list-books-title">
                        <h1>MyReads</h1>
                    </div>
                    <div className="list-books-content">
                        <Shelf title="Currently Reading" book={currentlyReading} onChangeShelf={this.changeShelf} />
                        <Shelf title="Want to read" book={wantToRead} onChangeShelf={this.changeShelf} />
                        <Shelf title="Read" book={read} onChangeShelf={this.changeShelf} />
                    </div>
                    <div className="open-search">
                        <button onClick={() => this.setState({ showSearchPage: true })}>Add a book</button>
                    </div>
                </div>
            </div>
        )
    }
}

export default Home
