import React, { Component } from 'react'
import { update } from '../BooksAPI'
export class Book extends Component {

    /*     updateShelf = e => {

            update(this.props, e.target.value)
            console.log(e.target.value)

        } */
    render() {
        //{ this.props.imageLinks['smallThumbnail'] }

        console.log(this.props.updateShelf)
        return (
            <div>

                <li>
                    <div className="book">
                        <div className="book-top">
                            <div className="book-cover"
                                style={{
                                    width: 128,
                                    height: 193,
                                    backgroundImage: `url(${this.props.imageLinks.thumbnail})`
                                }}></div>
                            <div className="book-shelf-changer">
                                <select value={this.props.shelf} onChange={(this.props, event.target.value)}>
                                    <option value="move" disabled>Move to...</option>
                                    <option value="currentlyReading">Currently Reading</option>
                                    <option value="wantToRead">Want to Read</option>
                                    <option value="read">Read</option>
                                    <option value="none">None</option>
                                </select>
                            </div>
                        </div>
                        <div className="book-title">{this.props.title}</div>
                        <div className="book-authors">{this.props.authors[0]}</div>
                    </div>
                </li>
            </div>
        )
    }
}

export default Book
